      if(self.user.cityOfOrigin == '' || self.user.cityOfOrigin === undefined) { self.checkValidate('Coorigin');}
      if(self.user.currentLocationCountry == '' || self.user.currentLocationCountry === undefined) { self.checkValidate('country');}
