      if(self.dateOfBirth == '' || self.dateOfBirth === null ){
        self.checkValidate('dob');
        valid = false;
      }
      else if(!self.validateDOB()) {
        MessageService.flush('Are you above 18?', 'warning');
        valid = false;
      }
      if(validCheck == false){
        MessageService.flush('Please fill all the mandatory fields!', 'warning');
        valid = false;
      }
