angular.module('FoodeeBuddee.provider.inAppBrowser',[])
.provider('AppBrowserProvider', function () {

    var ref;
    var defaultOptions = this.defaultOptions = {};

    this.setDefaultOptions = function (config) {
      defaultOptions = angular.extend(defaultOptions, config);
    };

    this.$get = ['$rootScope', '$q', '$window', '$timeout', function ($rootScope, $q, $window, $timeout) {
      return {
        open: function (url, target, requestOptions) {
          var q = $q.defer();

          if (requestOptions && !angular.isObject(requestOptions)) {
            q.reject("options must be an object");
            return q.promise;
          }

          var options = angular.extend({}, defaultOptions, requestOptions);

          var opt = [];
          angular.forEach(options, function (value, key) {
            opt.push(key + '=' + value);
          });
          var optionsString = opt.join();

          ref = $window.open(url, target, optionsString);

          ref.addEventListener('loadstart', function (event) {
            $timeout(function () {
              $rootScope.$broadcast('$cordovaInAppBrowser:loadstart', event);
            });
          }, false);

          ref.addEventListener('loadstop', function (event) {
            q.resolve(event);
            $timeout(function () {
              $rootScope.$broadcast('$cordovaInAppBrowser:loadstop', event);
            });
          }, false);

          ref.addEventListener('loaderror', function (event) {
            q.reject(event);
            $timeout(function () {
              $rootScope.$broadcast('$cordovaInAppBrowser:loaderror', event);
            });
          }, false);

          ref.addEventListener('exit', function (event) {
            $timeout(function () {
              $rootScope.$broadcast('$cordovaInAppBrowser:exit', event);
            });
          }, false);

          return q.promise;
        },

        close: function () {
          ref.close();
          ref = null;
        },

        show: function () {
          ref.show();
        },

        executeScript: function (details) {
          var q = $q.defer();

          ref.executeScript(details, function (result) {
            q.resolve(result);
          });

          return q.promise;
        },

        insertCSS: function (details) {
          var q = $q.defer();

          ref.insertCSS(details, function (result) {
            q.resolve(result);
          });

          return q.promise;
        }
      };
    }];
});
angular.module('FoodeeBuddee.services.location', [])
  .service('LocationService', ["$cordovaGeolocation", "MessageService", "$location", function($cordovaGeolocation, MessageService, $location) {
    var self = this;

    var options = {
      timeout : 10000,
      enableHighAccuracy: false // Set to true can cause location problems
    };

    self.locality = '';
    self.cityVal = '';
    self.stateVal = '';
    self.countryVal = '';
    self.zipVal = '';

    /**
     * @returns {*|HttpPromise|void}
     */
    self.currentLocation = function() {
      return $cordovaGeolocation
        .getCurrentPosition(options);
    };

    self.getLocationDetails = function(latitude, longitude) {
      var latlng = new google.maps.LatLng(latitude, longitude);
      var geocoder = new google.maps.Geocoder();
      geocoder.geocode(
        {'latLng': latlng},
        function(results, status) {
          if (status == google.maps.GeocoderStatus.OK) {
            if (results[0]) {
              for(var i = 0; i < results[0].address_components.length; i++) {
                if(results[0].address_components[i].types[0] == "country") {
                  self.countryVal = (results[0].address_components[i].long_name);
                }
              }
              var add= results[0].formatted_address ;
              var  value=add.split(",");
              count = value.length;
              if(value[count - 4] !== undefined){
                self.locality = value[count - 4];
              }
              if(value[count-2] !== undefined){
                self.stateVal = value[count-2];
              }
              if(value[count-3] !== undefined){
                self.cityVal = value[count-3];
              }
              pin = self.stateVal.split(" ");
              self.zipVal = pin[pin.length-1];
              self.stateVal = self.stateVal.replace(self.zipVal,' ');
            }
            else  {
              MessageService.flush('Could not find your location!, danger');
            }
          }
          else {
            console.log("Geocoder failed due to: " + status);
          }
        }
      );

    };

    return self;
  }]);
